document.addEventListener("alpine:init",()=>{Alpine.data("dropZone",()=>({onDragStart(t){t.target.classList.add("opacity-50"),t.dataTransfer.effectAllowed="move",t.dataTransfer.setData("application/json",JSON.stringify({id:t.target.dataset.id,status:t.target.dataset.status}))},onDragOver(t){t.preventDefault(),t.dataTransfer.dropEffect="move";const a=t.target.closest(".kanban-column");a&&a.classList.add("bg-neutral-700/50")},onDragEnd(t){t.target.classList.remove("opacity-50"),document.querySelectorAll(".kanban-column").forEach(a=>{a.classList.remove("bg-neutral-700/50")})},onDrop(t){const a=t.target.closest(".kanban-column");if(!a)return;a.classList.remove("bg-neutral-700/50");const e=JSON.parse(t.dataTransfer.getData("application/json")),s=a.dataset.status;e.status!==s&&window.Livewire.find(t.target.closest("[wire\\:id]").getAttribute("wire:id")).call("updateApplicationStatus",e.id,s)}}))});
